$(".comment-form").on("submit",function(e){console.log("onclick"),e.preventDefault();const t=$(this).attr("action");$.ajax({headers:{"X-CSRF-Token":$("#comment-csrf").attr("value")},type:"POST",url:t,data:$(this).serialize()}).done(function(e){$(".comments__header").text(`${e.commentsLength+1} Comments`);let t=$("<div>").addClass("comments__item-user").text(e.comment.user),m=$("<div>").addClass("comments__item-timestamp").text(e.formattedTimestamp),s=$("<div>").addClass("comments__item-message").text(e.comment.message);$("<li>").addClass("comments__item").append(t).append(m).append(s).appendTo(".comments__list");$(".comment-form")[0].reset()}).fail(function(e,t,m){console.log(t),console.log(m)})});